import React, { Component } from 'react';
import Data from '../assets/data/data.json';

class ApprovedSummaryContainer extends Component {

    calculateInterestAmount = () => {
        const interest = this.props.loanAmount * (Data.interestPercentage / 100);
        return interest;
    }

    render() {
        const {loanAmount} = this.props;
        return (
            <div className='approved-summary-container'>
                <p>Yout monthly repayment will be:</p>
                <p className='approved-repayment-amount'></p>

                <p>Principal:</p>
                <p className='approved-loan-amount'>{Data.currency}{loanAmount}</p>

                <p>Interest:</p>
                <p className='approved-interest-amount'>{Data.currency}{this.calculateInterestAmount}</p>

                <p>Total repayment:</p>
                <p className='approved-total-repayment-amount'></p>
            </div>
        )
    }
  }
  
  export default ApprovedSummaryContainer;
