import React, { Component } from 'react';
import Postcode from 'postcode-validator';
import LoanLogo from '../assets/svg/money-loan-logo.svg';
import Data from '../assets/data/data.json';
import InputField from './input-field';
import DropdownField from './dropdown-field';

const checkEmailValidity = RegExp(
    // eslint-disable-next-line 
    /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
);

const checkDateOfBirthValidity = RegExp(
    // eslint-disable-next-line 
    /^(0[1-9]|[12][0-9]|3[01])[\- \/.](?:(0[1-9]|1[012])[\- \/.](19|20)[0-9]{2})$/
);


class LoanForm extends Component {
    constructor() {
        super();
        this.state = {
            firstName: '',
            surname: '',
            dateOfBirth: '',
            email: '',
            postcode: '',
            companyName: '',
            annualTurnover: '',
            loanAmout: '',
            loanTerm: '',
            countryPostcode: 'UK',
            formErrors: {
                firstNameError: '',
                surnameError: '',
                dateOfBirthError: '',
                emailError: '',
                postcodeError: '',
                companyNameError: '',
                annualTurnoverError: '',
                loanAmoutError: '',
                loanTermError: '',
            }
          };
    } 

    handleSubmit = (e) => {
        e.preventDefault();
    }

    handleInputChange = (event) => {
        const { value, name } = event.target;
        let formErrors = { ...this.state.formErrors };

        switch (name) {
            case 'firstName':
              formErrors.firstNameError =
                value.length < 3 && (typeof value) !== 'string' ? 'minimum 3 characaters required' : '';
              break;
            case 'surname':
              formErrors.surnameError =
                value.length < 3 && (typeof value) !== 'string' ? 'minimum 3 characaters required' : '';
              break;
            case 'email':
              formErrors.emailError = checkEmailValidity.test(value) ? '' : 'invalid email address';
              break;
            case 'postcode':
              formErrors.postcodeError = value.length < 6 && Postcode.validate(value, this.state.countryPostcode) ? 'minimum 6 characaters required or invalid postcode' : '';
              break;
            case 'dateOfBirth':
                formErrors.dateOfBirthError = this.checkDateOfBirthIsValid(value) ? 'Field is empty or date format is wrong' : '';
              break;
            case 'companyName':
                formErrors.companyNameError = value.length < 3 && (typeof value) !== 'string' ? 'minimum 3 characaters required' : '';
              break;
            case 'loanAmout':
                formErrors.loanAmoutError = value.length < 5 && (typeof value) !== 'number' ? 'minimum loan amount is 10 000' : '';
              break;
            case 'annualTurnover':
                formErrors.password = value.length < 5 && (typeof value) !== 'number' ? 'minimum turnover amount is 15 000' : '';
              break;
            case 'loanTerm':
                formErrors.loanTermError = value !== '' ? 'minimum 6 characaters required' : '';
                console.log(value)
              break;
            default:
              break;
          }

        this.setState({ formErrors, [name]: value }, () => console.log(this.state));
    }

    // find values for the minimum and maximum loan month terms, if there are none in the data set default values
    generateMonthsOptions = () => {
        let min;
        let max;
        Data.loanForm.forEach(sections => {
            sections.fields.forEach(field => {
                ('minMonths' in field) ? min = field.minMonths : min = 12;
                ('maxMonths' in field) ? max = field.maxMonths + 1 : max = 61;
            });
        });

        return  Array.from(Array(max-min).keys()).map(i => min + i);
     } 

     checkDateOfBirthIsValid = (date) => {
        const today = new Date();
        date = Date.parse(date);

        if (date === "") {
            return false;
        }
        else if (checkDateOfBirthValidity.test(date)) {
            return true;
        }

        if (today <= date) {
            console.log("Current or future date is not allowed.");
            return false;
        }
    }

    render() {

      const {firstName, surname, dateOfBirth, email, postcode, companyName, annualTurnover, loanAmout, loanTerm} = this.state;

      return (
        <div className='loan-form'>
            <div className='loan-form-heading'>
                <img
                className='loan-logo-icon'
                alt='Loan logo icon'
                src={LoanLogo}
                />
                <div className='loan-form-title'>
                    <h2 className='loan-form-title-text'>Get your</h2>
                    <h2 className='loan-form-title-text'>Instant quote</h2>
                </div>
                <h3 className='loan-form-subtitle'>We only need a few details and it won't affect your credit score</h3>
            </div>
            <form onSubmit={this.handleSubmit}>
                <div className='loan-form-main-content'>
                    <InputField fieldLabel={'First name'} fieldValue={firstName} fieldName={'firstName'} onChange={this.handleInputChange} />
                    <InputField fieldLabel={'Surname'} fieldValue={surname} fieldName={'Surname'} onChange={this.handleInputChange} />
                    <InputField fieldLabel={'Email'} fieldValue={email} fieldName={'email'} fieldDetails={'You must be director of limited company registered at Company House'} onChange={this.handleInputChange} />
                    <InputField fieldLabel={'Date of birth'} fieldValue={dateOfBirth} fieldName={'dateOfBirth'} fieldDetails={'You must be 18 years or older to apply for ezbob loan'} onChange={this.handleInputChange} />
                    <InputField fieldLabel={'Residential postcode'} fieldValue={postcode} fieldName={'postcode'} fieldDetails={'You must be UK resident'} onChange={this.handleInputChange} />
                    <InputField fieldLabel={'Company name'} fieldValue={companyName} fieldName={'companyName'} onChange={this.handleInputChange} />
                    <InputField fieldLabel={'Annual turnover'} fieldValue={annualTurnover} fieldName={'annualTurnover'} fieldDetails={'Your company must have a current annual turnover of 15k or moret'} onChange={this.handleInputChange} />
                    <InputField fieldLabel={'Loan ammount'} fieldValue={loanAmout} fieldName={'loanAmout'} fieldDetails={'£10,000 - £150,000'} onChange={this.handleInputChange} />

                    <DropdownField fieldLabel={'Loan term'} fieldValue={loanTerm} fieldName={'loanTerm'} onChange={this.handleInputChange} />

                    <div className='input-field-container'>
                        <p className='input-field-text'>Loan term</p>
                        <select name='loanTerm'
                                value={loanTerm}
                                className={`input-field dropdown`}
                                onChange={this.handleInputChange}>
                            <option></option>
                            {this.generateMonthsOptions().map((option, index) => {
                                return (
                                    <option key={index}>{option} months</option>
                                );
                            })}
                        </select>
                        <p className='input-filed-details'>12 months - 60 months</p>
                    </div>

                </div>
                <div className='loan-form-consent-container'>
                    <p>{Data.loanForm.map(section => {
                        return (section.consent);
                       })}</p>
                </div>
                <div className='loan-form-submit-container'>
                    <button className='loan-form-submit-button' type='submit' onClick={this.handleSubmit}>Consent & Submit</button>
                </div>
            </form>
        </div>
      );
    }
  }
  
  export default LoanForm;
