import React, { Component } from 'react';

class InputField extends Component {

    generateMonthsOptions(min, max) {
       return  Array.from(Array(max-min).keys()).map(i => min + i);
    }

    render() {
    const {title, details, type, name, value, minMonth, maxMonth} = this.props;

        if(type === 'text') {
            return (
                <div className='input-field-container'>
                    <p className='input-field-text'>{title}</p>
                    <input type="text" className='input-field' name={name} value={value} required placeholder='Start typing...' />
                    <p className='input-filed-details'>{details}</p>
                </div>
            );
        } else {
            return (
                <div className='input-field-container'>
                    <p className='input-field-text'>{title}</p>
                    <select className={`input-field ${type}`}>
                        <option>Please select</option>
                        {this.generateMonthsOptions(minMonth, maxMonth).map((option, index) => {
                            return (
                                <option key={index}>{option} months</option>
                            );
                        })}
                    </select>
                    <p className='input-filed-details'>{details}</p>
                </div>
            );
        }
    }
  }
  
  export default InputField;
