import React, { Component } from 'react';
import Data from '../assets/data/data.json';

class InputField extends Component {

    generateMonthsOptions(min, max) {
       return  Array.from(Array(max-min).keys()).map(i => min + i);
    }

    render() {
    const {title, details, type, minMonth, maxMonth} = this.props;

        if(type === 'text') {
            return (
                <div className='input-field-container'>
                    <p className='input-field-text'>{title}</p>
                    <input type="text" className='input-field' name="name" required placeholder='Start typing...' />
                    <p className='input-filed-details'>{details}</p>
                </div>
            );
        } else {
            return (
                <div className='input-field-container'>
                    <select>
                        <option></option>
                        {generateMonthsOptions(minMonth, maxMonth).map((option, index) => {
                            return (
                                <option>`${option} months`</option>
                            );
                        })}
                    </select>
                </div>
            );
        }
    }
  }
  
  export default InputField;
