import React, { Component } from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import LoanForm from './components/loan-form';
import LoanApproved from './components/loan-approved';
import LoanDenied from './components/loan-denied';
import './App.css';

class App extends Component {

  constructor() {
    super();
      this.state = {
          loanAmout: '',
          loanTerm: ''
      } 
  }

  handleLoanAmount = (amount) => {
    this.setState({loanAmout: amount});
  }

  handleLoanTerm = (term) => {
    this.setState({loanTerm: term});
  }

  render() {
    const {loanAmout, loanTerm} = this.state;
    return (
      <Router>
        <div className="App">
        <Switch>
          <Route exact path="/" render={props => <LoanForm handleLoanTerm={this.handleLoanTerm} handleLoanAmount={this.handleLoanAmount} {...props} />} />
          <Route exact path="/approval" render={props => <LoanApproved loanAmount={loanAmout} loanTerm={loanTerm} {...props}/> } />
          <Route exact path="/denied" render={props => <LoanDenied />} />
        </Switch>
        </div>
      </Router>
    );
  }
}

export default App;
